document.getElementById("navigation-btn-open").onclick=function(){document.getElementById("navigation-list").classList.add("navigation__list--active")},document.getElementById("navigation-btn-close").onclick=function(){document.getElementById("navigation-list").classList.remove("navigation__list--active")};for(var navigation=document.getElementById("navigation-list"),navigationItems=navigation.getElementsByClassName("navigation__link"),i=0;i<navigationItems.length;i++)navigationItems[i].addEventListener("click",function(){removeActiveClass("navigation__link--active"),this.classList.add("navigation__link--active")});function removeActiveClass(i){for(var n=0;n<navigationItems.length;n++)navigationItems[n].classList.remove(i)}